{"IncludesReferencedQueries":false,"RootFormulaText":"let
    Source = Table.Combine(
        {
			Table.Unpivot(
					Table.FromRecords({ Record.SelectFields(ConnectionConfig, { "ConnectionType", "Host:Port", "Login", "Namespace" }) })
					, { "ConnectionType", "Host:Port", "Login", "Namespace" }
					, "Name"
					, "Value"
			)
			, Table.FromRecords(
					{   
						[ Name = "SSL connection"
						, Value = Text.Contains(Text.Lower(ConnectionConfig[#"Host:Port"]), "https") ]
					}
            )
            , Table.FromRecords(
                    {   
                        [ Name = "TransactionLog"
                        , Value = SettingsConfig[TransactionLogPeriodInDays] >= 0 ]
                    }
            )
        }
    )
